#include &lt;cantera/Cantera.h&gt;
#include &lt;cantera/IdealGasMix.h&gt;
#include &lt;cantera/transport.h&gt; 
#include &lt;cantera/kernel/KineticsFactory.h&gt;
#include &lt;cantera/onedim.h&gt;

int main(int argc, char* argv[]) 
{
  try{
    IdealGasMix gas(&quot;gri30.xml&quot;,&quot;gri30&quot;);
    doublereal temp = 500.0;
    doublereal pres = 2.0*OneAtm;
    gas.setState_TPX(temp, pres, &quot;CH4:1.0, O2:2.0, N2:7.52&quot;);

    Transport* tr=newTransportMgr(&quot;Mix&quot;, &gas);
    Kinetics* kn=newKineticsMgr(&quot;GRI30&quot;);

    doublereal z[] = {0.0, 0.05, 0.1, 0.15, 0.2};

    StFlow flow(&gas);
    flow.setupGrid(5, z);
    flow.setKinetics(*kn);
    flow.setTransport(*tr, false);
    flow.setPressure(0.05*OneAtm);

    Inlet1D inlet;
    inlet.setMoleFractions(&quot;CH4:1, O2:2, N2:7.52&quot;);
    inlet.setMdot(0.1);
    inlet.setTemperature(380);

    Surf1D surf;
    surf.setTemperature(600);

    vector&lt;Domain1D*&gt; domains;
    domains.push_back(&inlet);
    domains.push_back(&flow);
    domains.push_back(&surf);

    Sim1D flamesim(domains);
    int dom=-1;
    doublereal ratio = 5.0, slope = 0.1, curve = 0.2, prune = 0.05;
    flamesim.setRefineCriteria(dom, ratio, slope, curve, prune);
    flamesim.showSolution(cout);
    //    flamesim.eval(-1);
    //    flamesim.solve(1,1);
    return 0;
  }
  catch(CanteraError) {
        showErrors();
    }
}
